import React, { Fragment } from "react";
import Signature from "../../../interfaces/Signature";
import DishCard from "../../layout/card/DishCard";
import "./HomePage.scss";

interface SignatureProps {
  signatures: Signature[];
}

const SignatureItem: React.FC<Signature> = ({
  key,
  restaurantName,
  signatureName,
  signatureImage,
  signatureDescription,
  icon,
  price,
}: Signature) => {
  return (
    <div className="signature-container">
      <div className="restaurant-title">{restaurantName}</div>
      <DishCard img={signatureImage} title={signatureName} detail={signatureDescription} icon={restaurantDetails.icon} price={restaurantDetails.price} key={restaurantDetails.key}></DishCard>
      <div className="data-item">
        <img src={signatureImage} alt="img" className="dish-img"></img>
        <div className="data-content">
          <p className="dish-bold">{signatureName}</p>
          <p className="dish-description">{signatureDescription}</p>
          <img src={icon} alt="img" className="dish-icon"></img> <br></br>
          <div className="wrapper">
            <div className="outter"></div>
            <span className="outter-span">
              <span className="ils">â‚ª</span>
              {price}
            </span>
            <div className="outter"></div>
          </div>
        </div>
      </div>
    </div>
  );
};

const SignatureDish: React.FC<SignatureProps> = ({
  signatures,
}: SignatureProps) => {
  return (
    <div className="data-container">
      {signatures.map((signature) => {
        return (
          <SignatureItem
            key={signature.key}
            restaurantName={signature.restaurantName}
            signatureName={signature.signatureName}
            signatureDescription={signature.signatureDescription}
            icon={signature.icon}
            price={signature.price}
            signatureImage={signature.signatureImage}
          ></SignatureItem>
        );
      })}
    </div>
  );
};

export default SignatureDish;
