import { useParams } from 'react-router-dom';
import dummy from '../../DummyData';
import './DishPage.scss';
import Input from '../../layout/form/Input';
import line from "../../../assets/line-7-copy-2.png"
import Footer from '../../footer/Footer';
import { useState } from 'react';
import CloseButton from '../../layout/button/CloseButton';

const DishPage = () => {
    const params:any= useParams();
    const dishDetails= dummy.filter(item => item.signatureName == params.signatureName)[0];
    const [quantityNumber, setQuantitynumber]= useState(1);
    const inputsContainer= (content:string) => {
        return(
        <div>
            <p>{content}</p>
            <img src={line} alt="line" className='line'></img>
        </div>)
        }
  return (
    <div>
    <CloseButton onClick={() => back(false)}></CloseButton>

    <div className="dish-details">
      <img src={dishDetails.signatureImage} alt="img" className="dish-page-img" srcSet={`${dishDetails.signatureDesktopImage} 800w`}></img>
      <div className="data-content">
        <h3 className="dish-title">{dishDetails.signatureName}</h3>
        <p className="dish-page-description">{dishDetails.signatureDescription}</p>
        <img src={dishDetails.icon} alt="img" className="dish-page-icon"></img> <br></br>
        <div className="wrapper-dish-page">
          <div className="outter"></div>
          <span className="outter-span">
            <span className="ils">₪</span>
            {dishDetails.price}
          </span>
          <div className="outter"></div>
        </div>
      </div>
    </div>
    {inputsContainer('Choose a side')}
    <p>{dishDetails.side.map((side=> <Input content={side} type={'radio'} placeholder={''}/>))}</p>
    {inputsContainer('Changes')}
    <p>{dishDetails.changes.map((change=> <Input content={change} type={'checkbox'} placeholder={''}/>))}</p>
    {inputsContainer('Quantity')}
    <div className='quantity-container'><button>-</button> <span className='quantity-number'>{quantityNumber}</span><button>+</button></div>
    <button className='add-to-bag-btn'>ADD TO BAG</button>
    <Footer />
    </div>
  )
}

export default DishPage;
