import React from "react";
import Popular from "../../../interfaces/Popular";
import "./HomePage.scss";
import Slider from "react-slick";
import SlideArrow from "../../layout/slide-arrow/SlideArrow";

interface PopularProps {
  populars: Popular[];
}

const PopularItem: React.FC<Popular> = ({
  restaurantName,
  chefName,
  restaurantImage,
}: Popular) => {
  return (
    <div className="data-item popular-item">
      <img src={restaurantImage} alt="img" className="restaurant-img"></img>
      <div className="restaurant-content">
        <h2 className="restaurant-name">{restaurantName}</h2>
        <p className="chef-name">{chefName}</p>
      </div>
    </div>
  );
};

  const PopularRestaurant: React.FC<PopularProps> = ({
    populars,
  }: PopularProps) => {
    const settings = {
      dots: true,
      infinite: true,
      slidesToShow: 4,
      slidesToScroll: 1,
      nextArrow: (
        <SlideArrow className={""} style={{ undefined }} onClick={undefined} />
      ),
      prevArrow: (
        <SlideArrow className={""} style={{ undefined }} onClick={undefined} />
      ),
      responsive: [
        {
          breakpoint: 900,
          settings: {
            slidesToShow: 3,
          },
        },
        {
          breakpoint: 667,
          settings: {
            slidesToShow: 2,
          },
        },
        {
          breakpoint: 490,
          settings: {
            slidesToShow: 1,
          },
        },
      ],
    };
console.log(populars);
