import React from "react";
import Popular from "../../../interfaces/Popular";
import "./HomePage.scss";
import Slider from "react-slick";
import SlideArrow from "../../layout/slide-arrow/SlideArrow";

interface PopularProps {
  populars: Popular[];
}

const PopularItem: React.FC<Popular> = ({
  restaurantName,
  chefName,
  restaurantImage,
}: Popular) => {
  return (
    <div className="data-item popular-item">
      <img src={restaurantImage} alt="img" className="restaurant-img"></img>
      <div className="restaurant-content">
        <h2 className="restaurant-name">{restaurantName}</h2>
        <p className="chef-name">{chefName}</p>
      </div>
    </div>
  );
};

const PopularRestaurant: React.FC<PopularProps> = ({
  populars,
}: PopularProps) => {
  const SignatureDish: React.FC<PopularProps> = ({
    populars,
  }: PopularProps) => {
    const settings = {
      dots: true,
      infinite: true,
      slidesToShow: 4,
      slidesToScroll: 1,
      nextArrow: (
        <SlideArrow className={""} style={{ undefined }} onClick={undefined} />
      ),
      prevArrow: (
        <SlideArrow className={""} style={{ undefined }} onClick={undefined} />
      ),
      responsive: [
        {
          breakpoint: 1100,
          settings: {
            slidesToShow: 4,
          },
        },
        {
          breakpoint: 900,
          settings: {
            slidesToShow: 3,
          },
        },
        {
          breakpoint: 667,
          settings: {
            slidesToShow: 2,
          },
        },
        {
          breakpoint: 455,
          settings: {
            slidesToShow: 1.5,
          },
        },
        {
          breakpoint: 375,
          settings: {
            slidesToShow: 1,
          },
        },
      ],
    };

    return (
      <div className="div">
        <Slider {...settings}>
          {populars.map((item) => {
            return (
              <PopularItem
                key={item.key}
                chefName={item.chefName}
                restaurantName={item.restaurantName}
                restaurantImage={item.restaurantImage}
              ></PopularItem>
            );
          })}
        </Slider>
      </div>
    );
  };
};
export default PopularRestaurant;
