import React, { useState } from "react";
import BeigeCard from "../../layout/card/BeigeCard";
import { connect } from "react-redux";
import { addData } from "../../../store/action";
import './AllRestaurants.scss';
import { Link } from "react-router-dom";

const RestaurantsGrid: React.FC<{ addData: any; data: any }> = (props) => {
          // const [restaurantName, setRestaurantName]= useState<string | null>('')
          // let restaurant: React.SetStateAction<string | null>;
  return (
    <div className="restaurants-grid">
      {addData.payload.map((item) => {
        return (
         <Link to={`/RestaurantPage/${item.restaurantName}`} className="to-restaurant-btn"><BeigeCard
            key={item.key}
            title={item.restaurantName}
            detail={item.chefName}
            img={item.restaurantImage}
          ></BeigeCard></Link> 
        );
      })}
    </div>
  );
};

const mapStateToProps = (state: { data: any }) => ({ data: state.data });

const mapDispatchToProps = (
  dispatch: (arg0: { type: string; payload: any }) => any
) => {
  return {
    addData: () => dispatch(addData),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(RestaurantsGrid);
