import { useParams } from 'react-router-dom';
import Footer from '../../footer/Footer';
import Header from '../../header/Header';
import dummy from '../../DummyData';
import clock from '../../../assets/clock-icon.svg';
import NavLinkTemplate from '../../layout/button/NavLinkTemplate';
import DishCard from '../../layout/card/DishCard';
import './RestaurantPage.scss';
import { Fragment } from 'react';
import { Link } from 'react-router-dom';

const RestaurantPage = () => {
    const params:any= useParams();
    const restaurantDetails= dummy.filter(item => item.restaurantName == params.restaurantName);
  return (
    <Fragment>
        <Header />
        <img src={restaurantDetails[0].desktopImage} alt="restaurant" className='restaurant-page-img' srcSet={`${restaurantDetails.bigImage} 800w`}></img>
        <h1>{params.restaurantName}</h1>
        <h2>{restaurantDetails[0].chefName}</h2>
        <img src={clock} alt="clock"></img> <span>Open now</span>
        <nav className="categories-div">
            <NavLinkTemplate content={'Breakfast'} to='/breakfast' />
            <NavLinkTemplate content={'Lunch'} to='/lunch' />
            <NavLinkTemplate content={'Dinner'} to='/Dinner' />
        </nav>
        {restaurantDetails.map((item) => {
        return (
          <Link
            to={`/DishPage/${item.signatureName}`}
            className="to-restaurant-btn">
                  <div className='restaurants-container'> <DishCard img={item.signatureImage} signatureDesktopImage={item.signatureDesktopImage} title={item.signatureName} detail={item.signatureDescription} icon={item.icon} price={item.price} key={restaurantDetails.key}></DishCard></div>
          </Link>
        );
      })}
        <Footer />
    </Fragment>
  )
}

export default RestaurantPage;
