import { useParams } from "react-router-dom";
import Footer from "../../footer/Footer";
import Header from "../../header/Header";
import dummy from "../../DummyData";
import clock from "../../../assets/clock-icon.svg";
import NavLinkTemplate from "../../layout/button/NavLinkTemplate";
import DishSmallCard from "../../layout/card/DishSmallCard";
import "./RestaurantPage.scss";
import { Fragment, useState } from "react";


const RestaurantPage = () => {
  const [openDishCard, setOpenDishCard]= useState(false);

  const openDishCardHandler = (dishName: string) => {
    setOpenDishCard(true);
    console.log(openDishCard);
   };

  const params: any = useParams();
  const restaurantDetails = dummy.filter(
    (item) => item.restaurantName == params.restaurantName
  );
  const mealsTypes: string[] = ["Breakfast", "Lunch", "Dinner"];
  return (
    <Fragment>
      <Header />
      {openDishCard && (<div>        <div className="dish-details">
          <img
            src={restaurantDetails.signatureImage}
            alt="img"
            className="dish-page-img"
            srcSet={`${restaurantDetails.signatureDesktopImage} 800w`}
          ></img>
          <div className="data-content">
            <h3 className="dish-title">{restaurantDetails.signatureName}</h3>
            <p className="dish-page-description">
              {dishDetails.signatureDescription}
            </p>
            <img
              src={dishDetails.icon}
              alt="img"
              className="dish-page-icon"
            ></img>{" "}
            <br></br>
            <div className="wrapper-dish-page">
              <div className="outter"></div>
              <span className="outter-span">
                <span className="ils">â‚ª</span>
                {dishDetails.price}
              </span>
              <div className="outter"></div>
            </div>
          </div>
        </div></div>)}
      <div>
        <img
          src={restaurantDetails[0].desktopImage}
          alt="restaurant"
          className="restaurant-page-img"
          srcSet={`${restaurantDetails[0].bigImage} 800w`}
        ></img>
        <h1>{params.restaurantName}</h1>
        <h2>{restaurantDetails[0].chefName}</h2>
        <img src={clock} alt="clock"></img> <span>Open now</span>
        <nav className="categories-div">
          {mealsTypes.map((type) => (
            <NavLinkTemplate content={type} to={`/${type}`} />
          ))}
        </nav>
        <div className="restaurants-container">
          {restaurantDetails.map((item) => {
            return (
              <button className="to-dish-btn" onClick={()=> openDishCardHandler(item.signatureName)}>
                <div className="restaurant-item">
                  <DishSmallCard
                    img={item.signatureImage}
                    signatureDesktopImage={item.signatureDesktopImage}
                    title={item.signatureName}
                    detail={item.signatureDescription}
                    price={item.price}
                    key={item.key}
                  ></DishSmallCard>
                </div>
              </button>
            );
          })}
        </div>
      </div>
      <Footer />
    </Fragment>
  );
};


export default RestaurantPage;
