import { useParams } from 'react-router-dom';
import Footer from '../../footer/Footer';
import Header from '../../header/Header';
import dummy from '../../DummyData';
import clock from '../../../assets/clock-icon.svg';
import NavLinkTemplate from '../../layout/button/NavLinkTemplate';
import DishCard from '../../layout/card/DishCard';
import './RestaurantPage.scss';
import { Fragment } from 'react';

const RestaurantPage = () => {
    const params:any= useParams();
    const restaurantDetails= dummy.filter(item => item.restaurantName == params.restaurantName)[0];
  return (
    <Fragment>
        <Header />
        <header />
        <img src={restaurantDetails.desktopImage} alt="restaurant" className='restaurant-page-img'></img>
        <h1>{params.restaurantName}</h1>
        <h2>{restaurantDetails.chefName}</h2>
        <img src={clock} alt="clock"></img> <span>Open now</span>
        <nav>
            <NavLinkTemplate content={'Breakfast'} to='/breakfast' />
            <NavLinkTemplate content={'Lunch'} to='/lunch' />
            <NavLinkTemplate content={'Dinner'} to='/Dinner' />
        </nav>
        <DishCard img={restaurantDetails.signatureImage}  title={restaurantDetails.signatureName} detail={restaurantDetails.signatureDescription} icon={restaurantDetails.icon} price={restaurantDetails.price} key={restaurantDetails.key}></DishCard>
        <Footer />
    </Fragment>
  )
}

export default RestaurantPage;
