import React from "react";
import Popular from "../../../interfaces/Popular";
import "./HomePage.scss";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

interface PopularProps {
  populars: Popular[];
}

const PopularItem: React.FC<Popular> = ({
  restaurantName,
  chefName,
  restaurantImage,
}: Popular) => {
  return (
    <div className="data-item">
      <img src={restaurantImage} alt="img" className="restaurant-img"></img>
      <div className="restaurant-content">
        <h2 className="restaurant-name">{restaurantName}</h2>
        <p className="chef-name">{chefName}</p>
      </div>
    </div>
  );
};

const responsive= {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 5
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 3
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1
  }
};

const PopularRestaurant: React.FC<PopularProps> = ({
  populars,
}: PopularProps) => {
  return (
    <Carousel responsive={responsive}>
    <div className="data-container">
      {populars.map((item) => {
        return (
          <PopularItem
            key={item.key}
            chefName={item.chefName}
            restaurantName={item.restaurantName}
            restaurantImage={item.restaurantImage}
          ></PopularItem>
        );
      })}
    </div>
    </Carousel>
  );
};

export default PopularRestaurant;
