import React, { Fragment, useState } from 'react'
import './In'
const Input: React.FC<{content: string, type:string, placeholder:string}> = (props) => {
    const [enteredInput, setEnteredInput]= useState('');
    const changeInputHandler= (event:React.FormEvent<HTMLInputElement>) => {
        event.preventDefault();
        setEnteredInput(event.currentTarget.value);
    }
  return (
      <Fragment>
          <label htmlFor={props.content}>{props.content}</label>
          <input type={props.type} name={props.content} className="input" value={enteredInput} placeholder={props.placeholder} onChange={changeInputHandler} required></input><br />
      </Fragment>
  )
}

export default Input;
