import React from "react";
import Popular from "../../../interfaces/Popular";
import "./HomePage.scss";
import Slider from 'react-slick';

interface PopularProps {
  populars: Popular[];
}

const PopularItem: React.FC<Popular> = ({
  restaurantName,
  chefName,
  restaurantImage,
}: Popular) => {
  return (
    <div className="data-item">
      <img src={restaurantImage} alt="img" className="restaurant-img"></img>
      <div className="restaurant-content">
        <h2 className="restaurant-name">{restaurantName}</h2>
        <p className="chef-name">{chefName}</p>
      </div>
    </div>
  );
};

const PopularRestaurant: React.FC<PopularProps> = ({
  populars,
}: PopularProps) => {
  const settings = {
    dots: true,
    infinite: false,
    slidesToShow: 4,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 3,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          initialSlide: 1
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };

  return (
    <div>
      <Slider {...settings}>
        {populars.map((item) => {
          return (
            <PopularItem
              key={item.key}
              chefName={item.chefName}
              restaurantName={item.restaurantName}
              restaurantImage={item.restaurantImage}
            ></PopularItem>
          );
        })}
      </Slider>
    </div>
  );
};

export default PopularRestaurant;
