import logo from "../../assets/group-10.png";
import hamburger from "../../assets/group-13.png";
import search from "../../assets/search-icon.png";
import user from "../../assets/user-icon-copy.png";
import bag from "../../assets/group-2-copy.png";
import "./Header.scss";
import { Fragment, useState } from "react";
import React from "react";
import NavLinkTemplate from "../layout/button/NavLinkTemplate";
import { Link } from "react-router-dom";

const Header = () => {
  const content:string[]= ["Chefs", "All Restaurants", "Sign In", "Contact Us", "Terms Of Use"];
  const navigateTo= content.map((item)=> item.replace(/\s/g, "")); 

  const [showNavBar, setShowNavBar] = useState(false);
  const openNavBarHandler = (event: React.MouseEvent) => {
    event.preventDefault();
    setShowNavBar(true);
  };
  return (
    <Fragment>
      <nav className="top-nav">
        {showNavBar && (
          <button
            className="close-nav-bar"
            onClick={() => setShowNavBar(false)}
          >
            X
          </button>
        )}
        {!showNavBar && (
          <>
            <img
              src={hamburger}
              alt="hamburger"
              className="hamburger"
              onClick={openNavBarHandler}
            ></img>
            <>
              <Link to="/"><img src={logo} alt="logo" className="logo"></img></Link>
              <div className="right-icons">
                <img src={search} alt="search"></img>
                <Link to="/SignIn"><img src={user} alt="user"></img></Link>
                <img src={bag} alt="bag"></img>
              </div>
            </>
          </>
        )}
      </nav>
      {showNavBar && (
        <div className="hamburger-bar">

          <hr />
          <li>
            <NavLinkTemplate
              content={"Sign in"}
              to={"/SignIn"}
            ></NavLinkTemplate>
          </li>
          <li>
            <NavLinkTemplate
              content={"Contact us"}
              to={"/ContactUs"}
            ></NavLinkTemplate>
          </li>
          <li>
            <NavLinkTemplate
              content={"Terms of Use"}
              to={"/TermsOfUse"}
            ></NavLinkTemplate>
          </li>
        </div>
      )}
    </Fragment>
  );
};

export default Header;
