import React from "react";
import Popular from "../../../interfaces/Popular";
import "./HomePage.scss";
import Slider from "react-slick";
import SlideArrow from "../../layout/slide-arrow/SlideArrow";
import { Link } from "react-router-dom";

interface PopularProps {
  populars: Popular[];
}

const PopularItem: React.FC<Popular> = ({
  restaurantName,
  chefName,
  restaurantImage,
  desktopImage
}: Popular) => {
  return (
    <Link to={`/RestaurantPage/${restaurantName}`} className="to-restaurant-btn">
    <div className="data-item popular-item">
      <img src={restaurantImage} alt="img" className="restaurant-img" srcSet={`${desktopImage} 800w`}></img>
      <div className="restaurant-content">
        <h2 className="restaurant-name">{restaurantName}</h2>
        <p className="chef-name">{chefName}</p>
      </div>
    </div>
    </Link>
  );
};
