  const PopularRestaurant: React.FC<PopularProps> = ({
    populars,
  }: PopularProps) => {
    const settings = {
      dots: true,
      infinite: true,
      slidesToShow: 3,
      slidesToScroll: 1,
      nextArrow: (
        <SlideArrow className={""} style={{ undefined }} onClick={undefined} />
      ),
      prevArrow: (
        <SlideArrow className={""} style={{ undefined }} onClick={undefined} />
      ),
      responsive: [
        {
          breakpoint: 1000,
          settings: {
            slidesToShow: 2,
          },
        },
        {
          breakpoint: 490,
          settings: {
            slidesToShow: 1,
          },
        },
      ],
    };
const smallerArr= populars.slice(0,3);
    return (
      <div className="slider-div">
        <Slider {...settings}>
          {smallerArr.map((item) => {
            return (
              <PopularItem
                key={item.key}
                chefName={item.chefName}
                restaurantName={item.restaurantName}
                restaurantImage={item.restaurantImage}
                desktopImage={item.desktopImage}
              ></PopularItem>
            );
          })}
        </Slider>
      </div>
    );
  };

export default PopularRestaurant;
